import React, { Component } from "react";
import { NavigationContainer } from "@react-navigation/native";
import { createBottomTabNavigator } from "@react-navigation/bottom-tabs";
import { createStackNavigator } from "@react-navigation/stack";
import Ionicons from "react-native-vector-icons/Ionicons";
import { View, Text, StyleSheet, TextInput, Button } from "react-native";
import All from "./src/All";
import DoneScreenBis from "./src/DoneScreen";
import TodoScreenBis from "./src/TodoScreen";

const Tab = createBottomTabNavigator();
const HomeStack = createStackNavigator();
const SettingsStack = createStackNavigator();

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      list: [],
      text: "",
      list_valid: [],
      description: "",
      listDescription: [],
    };
  }

  deleteItem = (index) => {
    const tmp = [...this.state.list];
    const tmp_des = [...this.state.listDescription];
    tmp.splice(index, 1);
    tmp_des.splice(index, 1);
    this.setState({ list: tmp, listDescription: tmp_des }, () => {
      this.storeData();
    });
  };

  deleteValidItem = (index) => {
    const tmp = [...this.state.list_valid];
    tmp.splice(index, 1);
    this.setState({ list_valid: tmp }, () => {
      this.storeData();
    });
  };

  addItem = () => {
    const tmp = [...this.state.list];
    const tmp_des = [...this.state.listDescription];
    tmp.push(this.state.text);
    tmp_des.push(this.state.description);
    this.setState({ list: tmp, text: "", listDescription: tmp_des }, () => {
      this.storeData();
    });
  };

  ValidItem = (index, text) => {
    this.deleteItem(index);
    const tmp = [...this.state.list_valid];
    tmp.push(text);
    this.setState({ list_valid: tmp });
  };

  storeData = async () => {
    await AsyncStorage.setItem("List", JSON.stringify(list));
    await AsyncStorage.setItem(
      "ListDesc",
      JSON.stringify(listDescription)
    );
    await AsyncStorage.setItem("ListValid", JSON.stringify(list_valid));
  };

  getData = async (key) => {
    const value = await AsyncStorage.getItem(key);
    if (!value) {
      return [];
    }
    const value_bis = JSON.parse(value);
    return value_bis;
  };

  AllScreen() {
    return (
      <View style={{ flex: 1 }}>
        <All
          list={this.state.list}
          list_valid={this.state.list_valid}
          listDescription={this.state.listDescription}
          text={this.state.text}
          description={this.state.description}
          ValidItem = {this.ValidItem()}
          addItem = {this.addItem()}
          deleteValidItem = {this.deleteValidItem()}
          deleteItem = {this.deleteItem()}
        />
      </View>
    );
  }

  DoneScreen() {
    return (
      <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
        {/*<DoneScreenBis key="doneScreen" list_valid={this.state.list_valid} />*/}
      </View>
    );
  }

  TodoScreen() {
    return (
      <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
        {/*<TodoScreenBis key="todoScreen" list={this.state.list} />*/}
      </View>
    );
  }

  AllStackScreen() {
    return (
      <HomeStack.Navigator>
        <HomeStack.Screen name="All" component={this.AllScreen} />
      </HomeStack.Navigator>
    );
  }

  DoneStackScreen() {
    return (
      <SettingsStack.Navigator>
        <SettingsStack.Screen name="Done" component={this.DoneScreen} />
      </SettingsStack.Navigator>
    );
  }

  TodoStackScreen() {
    return (
      <SettingsStack.Navigator>
        <SettingsStack.Screen name="Todo " component={this.TodoScreen} />
      </SettingsStack.Navigator>
    );
  }
  render() {
    return (
      <NavigationContainer>
        <Tab.Navigator
          screenOptions={({ route }) => ({
            tabBarIcon: ({ focused, color, size }) => {
              let iconName;

              if (route.name === "All") {
                iconName = focused ? "list" : "list";
              } else if (route.name == "Done") {
                iconName = focused ? "checkbox" : "checkbox";
              } else {
                iconName = focused ? "checkbox-outline" : "checkbox-outline";
              }
              return <Ionicons name={iconName} size={size} color={color} />;
            },
          })}
          tabBarOptions={{
            activeTintColor: "purple",
            inactiveTintColor: "black",
          }}
        >
          <Tab.Screen
            name="All"
            component={this.AllStackScreen}
            options={{ title: "All" }}
          />
          <Tab.Screen name="Done" component={this.DoneStackScreen} />
          <Tab.Screen name="Todo" component={this.TodoStackScreen} />
        </Tab.Navigator>
      </NavigationContainer>
    );
  }
}
