import React from "react";
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
} from "react-native";
import { FontAwesome } from "@expo/vector-icons";
import { Ionicons } from "@expo/vector-icons";

const ListItem = ({ element, ValidItem, deleteItem }) => {
  return (
    <View>
      <View style={styles.item}>
        <TouchableOpacity onPress={() => ValidItem()}>
          <Ionicons name="checkmark-circle-outline" size={18} />
        </TouchableOpacity>
        <Text style={{ fontSize: 18 }}>{element.text}</Text>
        <TouchableOpacity onPress={() => deleteItem()}>
          <FontAwesome name="trash" size={18} />
        </TouchableOpacity>
      </View>
      <View style={styles.desc}>
        <Text style={styles.descText}>{element.description}</Text>
      </View>
    </View>
  );
};

const ListValidItem = ({ element, list_valid, setListValid }) => {
  const deleteValidItem = () => {
    console.log("delete");
    const index = list_valid.findIndex((e) => e === element);
    const tmp = [...list_valid];
    tmp.splice(index, 1);
    setListValid(tmp);
  };
  return (
    <View>
      <View style={styles.item}>
        <TouchableOpacity onPress={() => ValidItem()}>
          <Ionicons name="checkmark-circle" size={18} />
        </TouchableOpacity>
        <Text
          style={{
            fontSize: 18,
            textDecorationLine: "line-through",
            color: "grey",
          }}
        >
          {element.text}
        </Text>
        <TouchableOpacity onPress={() => deleteValidItem()}>
          <FontAwesome name="trash" size={18} />
        </TouchableOpacity>
      </View>
      <View style={styles.desc}>
        <Text
          style={
            (styles.descText,
            { textDecorationLine: "line-through", color: "grey" })
          }
        >
          {element.description}
        </Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    width: "100%",
    height: "100%",
    marginTop: "10%",
    fontFamily: "Courier",
  },
  item: {
    marginLeft: 15,
    marginRight: 15,
    marginBottom: 2,
    borderRadius: 5,
    flexDirection: "row",
    justifyContent: "space-between",
  },
  button: {
    padding: 12,
    flexDirection: "column",
    alignItems: "center",
    marginBottom: 5,
    marginRight: 20,
    marginLeft: 20,
    borderWidth: 1,
    borderRadius: 5,
    borderColor: "purple",
  },
  input: {
    height: 30,
    marginHorizontal: 5,
    paddingHorizontal: 5,
    marginBottom: 5,
  },
  fond: {
    marginLeft: 5,
    marginRight: 5,
    borderRadius: 5,
    marginBottom: 10,
  },
  desc: {
    alignItems: "center",
    marginBottom: 10,
    fontFamily: "Courier",
  },
  descText: {
    fontSize: 10,
    fontStyle: "italic",
  },
});

export { ListItem, ListValidItem };
