import React, { Component } from "react";
import { useState } from "react";
import {
  View,
  Text,
  TouchableOpacity,
  TextInput,
  StyleSheet,
  ScrollView,
  TextComponent,
  Button,
  TouchableWithoutFeedbackBase,
} from "react-native";
import { useFocusEffect } from "@react-navigation/native";
import { getData } from "./utils";
import {ListValidItem} from "./ListItem";

function DoneScreen(props) {
  const [list_valid, setList] = useState([]);
  
  useFocusEffect(() => {
    getData("ListValid").then((res_list) => {
      setList(res_list);
    });
  })

    const  Done = () => {
    return (
      <View style={styles.fond}>
        <Text> Termin√© </Text>      
        {list_valid.map((element, index) => {
          return <ListValidItem key="valid" element={element} list_valid={list_valid} setListValid={() => console.log("liste")}/>;
        })}
      </View>
    );
  };
  return (
    <View>
      <Done key="Done"/>
    </View>
  );
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: "#efefef",
      width: "100%",
      height: "100%",
      marginTop: "10%",
      justifyContent: "center",
    },
    item: {
      backgroundColor: "white",
      marginLeft: 15,
      marginRight: 15,
      marginBottom: 2,
      borderRadius: 5,
      flexDirection: "row",
      justifyContent: "space-between",
    },
    button: {
      padding: 12,
      justifyContent: "center",
      alignItems: "center",
      backgroundColor: "#FF7C5F",
      marginBottom: 5,
      marginRight: 20,
      marginLeft: 20,
      borderRadius: 5,
    },
    input: {
      height: 30,
      marginHorizontal: 5,
      paddingHorizontal: 5,
      marginBottom: 5,
      borderColor: "lightgrey",
    },
    fond: {
      marginLeft: 5,
      marginRight: 5,
      borderRadius: 5,
      backgroundColor: "#DCDCDC",
      marginBottom: 10,
    },
  });
  

export default DoneScreen;
